const AuthReducer = (state,action) => {
    switch(action.type){
        case "LOGIN START":
            return {
                user:null,
                isFetching:true,
                error:false,
            };
        case "LOGIN SUCCESS":
            return {
                user: action.payload,
                isFetching:false,
                error:false,
            };
        case "LOGIN_FAILURE":
            return {
                user:null,
                isFetching:false,
                error:true,
            };
        default: 
            return {...state};
    }
};
export default AuthReducer;
