import Home from './pages/home/Home';
import "./app.scss"
import Watch from './pages/watch/Watch';
import Register from './pages/register/Register';
import Login from './pages/login/Login';
import {
  BrowserRouter,
  Routes,
  Route,
  useNavigate
} from 'react-router-dom';
import { useContext } from "react";
import { AuthContext } from "./authContext/AuthContext";
const App = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  return (
    <BrowserRouter>
    <Routes>
    <Route exact path="/">
          {user ? <Home /> : navigate("/register")}
        </Route>
        <Route path="/register">
          {!user ? <Register /> : navigate("/")}
        </Route>
        <Route path="/login">{!user ? <Login /> : navigate("/")}</Route>
        {user && (
          <>
            <Route path="/movies">
              <Home type="movie" />
            </Route>
            <Route path="/series">
              <Home type="series" />
            </Route>
            <Route path="/watch">
              <Watch />
            </Route>
          </>
        )}
    </Routes>
  </BrowserRouter>
  );
};

export default App;
